{{(() => { const tableData = Tbl_nivelCatAmbCopyCopyCopy.processedTableData || []; return tableData.length > 0 ? tableData.map((currentRow, currentIndex) => (
(() => {
  const v = Number(currentRow.pontuacao_diferenca);
  if (!Number.isFinite(v)) return "transparent";

  // Normalização pela query (ajusta se usares outra fonte)
  const rows = Qry_pontuacaoCategoria.data ?? [];
  const values = rows.map(r => Number(r.pontuacao_diferenca)).filter(Number.isFinite);
  const maxAbs = Math.max(...values.map(x => Math.abs(x)), 1);

  // t = intensidade 0..1; usar "ease-out" para suavizar (t' = sqrt(t))
  const tLinear = Math.min(Math.abs(v) / maxAbs, 1);
  const t = Math.sqrt(tLinear); // curva mais suave

  if (v === 0) {
    // ZERO: amarelo mais "cheio" (menos claro, mais saturado)
    const hue = 55;          // amarelo limpo
    const saturation = 92;   // bem saturado
    const lightness = 75;    // menos claro que 95, fica mais amarelo
    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
  }

  if (v < 0) {
    // NEGATIVOS: laranja vivo (evitar castanho): hue 32–35, alta saturação,
    // lightness desce mas não demais (não ir abaixo de ~50–55)
    const hue = 33;                  // laranja (não castanho)
    const saturation = 95;           // bem saturado
    const lightness = 85 - 30 * t;   // 85% -> 55% (limite mantém laranja vivo)
    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
  }

  // POSITIVOS: verde mais claro e limpo
  {
    const hue = 120;                 // verde
    const saturation = 55;           // saturação moderada
    const lightness = 80 - 25 * t;   // 88% -> 64% (clarinho, não escuro)
    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
  }
})()
